import React, { Component } from 'react';
import 'whatwg-fetch';
import './css/stylesheet.css';
import ReactDOM from 'react-dom'
import { Route, Link, BrowserRouter as Router } from 'react-router-dom'


class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
      token: 0,
      username: '',
      password: '',
      email: '',
      fullName: '',
      message: ''
    };


  }

  componentDidMount() {
    const obj = 1 ;
    if (obj && obj.token) {
      const { token } = obj;
      // Verify token
      fetch('/api/account/verify?token=' + token)
        .then(res => res.json())
        .then(json => {
          if (json.success) {
            this.setState({
              token,
              isLoading: false
            });
          } else {
            this.setState({
              isLoading: false,
            });
          }
        });
    } else {
      this.setState({
        isLoading: false,
      });
    }
    fetch('result')
        .then(res => '[{"userID" : "Zach","userID" : "Trevor","userID" : "Phil","userID" : "Rob"}]')
        .then(res => res.json())
        .then((data) => {
          this.setState({userID : data})
        })
        .catch(console.log)
    this.timeoutHandle = setTimeout(()=>{
              // Add your logic for the transition
         }, 5000);
  }

  login() {

  	this.setState({
      isLoading: true,
    });

    fetch('/loginUser')
    .then(res => res.json())
    .then((data) => {
      this.setState({
        token: data,
        username: data,
        email: data,
        fullName: data,
        message: data
      })
    })

  }


  render() {
    const {
      isLoading,
      token,
      userID,
      password,
      email,
      fullName,
      message
    } = this.state;

    if (isLoading) {
      return (<div><p>Loading...</p></div>);
    }

    if (!token) {
      return (
        <div class="loginBox">
        	<h1>Sign In</h1>
          <br/>
          <input
            type="userID"
            placeholder="User ID"
          />
          <br />
          <input
            type="password"
            placeholder="Password"
          />
          <br />
          <button class="loginButton" onClick={this.login}><span>Sign In</span></button>
        </div>
      );
    }
  }
}

export default Login;
