import React, { Component } from 'react';
import 'whatwg-fetch';
import './css/stylesheet.css';
import { Route, Link, BrowserRouter as Router } from 'react-router-dom'

import UserIDS from './components/userids';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
      token: '',
      userID: []
    };


  }

  componentDidMount() {
    const obj = 1 ;
    if (obj && obj.token) {
      const { token } = obj;
      // Verify token
      fetch('/api/account/verify?token=' + token)
        .then(res => res.json())
        .then(json => {
          if (json.success) {
            this.setState({
              token,
              isLoading: false
            });
          } else {
            this.setState({
              isLoading: false,
            });
          }
        });
    } else {
      this.setState({
        isLoading: false,
      });
    }
    fetch('result')
        .then(res => '[{"userID" : "Zach","userID" : "Trevor","userID" : "Phil","userID" : "Rob"}]')
        .then(res => res.json())
        .then((data) => {
          this.setState({userID : data})
        })
        .catch(console.log)
    this.timeoutHandle = setTimeout(()=>{
              // Add your logic for the transition
         }, 5000);
  }

  login() {

  	this.setState({
      isLoading: true,
    });


  }


  render() {
    const {
      isLoading,
      token,
      userID,
    } = this.state;

    if (isLoading) {
      return (<div><p>Loading...</p></div>);
    }

    if (!token) {
      return (
        <div>
          <div>
          	<img src={ require('./images/favicon.png')} alt="FP" class="MainTitle"/>
          </div>
          <div>
          	 <button class="loginButton" onClick={this.onSignIn}>Log In</button>
          </div>
          <Router>
		    <div>
		      <ul>
		        <li>
		          <Link to="/">Log in</Link>
		        </li>
		      </ul>
		      <Route exact path="/App-Full" component={Home} />
		    </div>
		  </Router>
        </div>
      );
    }
  }
}

export default Home;
