import React, { Component } from 'react';
import 'whatwg-fetch';
import './css/stylesheet.css';

import UserIDS from './components/userids';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
      token: '',
      userID: []
    };

    this.onTextboxChangeFindOneValue = this.onTextboxChangeFindOneValue.bind(this);
  }

  componentDidMount() {
    const obj = 1 ;
    if (obj && obj.token) {
      const { token } = obj;
      // Verify token
      fetch('/api/account/verify?token=' + token)
        .then(res => res.json())
        .then(json => {
          if (json.success) {
            this.setState({
              token,
              isLoading: false
            });
          } else {
            this.setState({
              isLoading: false,
            });
          }
        });
    } else {
      this.setState({
        isLoading: false,
      });
    }
    fetch('result')
        .then(res => '[{"userID" : "Zach","userID" : "Trevor","userID" : "Phil","userID" : "Rob"}]')
        .then(res => res.json())
        .then((data) => {
          this.setState({userID : data})
        })
        .catch(console.log)
    this.timeoutHandle = setTimeout(()=>{
              // Add your logic for the transition
         }, 5000);
  }

  onTextboxChangeFindOneValue(event) {
    this.setState({
      findOneValue: event.target.value,
    });
  }



  render() {
    const {
      isLoading,
      token,
      userID,
    } = this.state;

    if (isLoading) {
      return (<div><p>Loading...</p></div>);
    }

    if (!token) {
      return (
        <div>
          <div>
          	<img src={ require('./images/favicon.png')} alt="FP" class="MainTitle"/>
          </div>
          <div>
          	 <button onClick={this.onSignIn}>Log In</button>
          </div>
        </div>
      );
    }
  }
}

export default Home;
