import React, { Component } from 'react';
import 'whatwg-fetch';

import UserIDS from './components/userids';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
      token: '',
      userID: []
    };

    this.onTextboxChangeFindOneValue = this.onTextboxChangeFindOneValue.bind(this);
  }

  componentDidMount() {
    const obj = 1 ;
    if (obj && obj.token) {
      const { token } = obj;
      // Verify token
      fetch('/api/account/verify?token=' + token)
        .then(res => res.json())
        .then(json => {
          if (json.success) {
            this.setState({
              token,
              isLoading: false
            });
          } else {
            this.setState({
              isLoading: false,
            });
          }
        });
    } else {
      this.setState({
        isLoading: false,
      });
    }
    fetch('result')
        .then(res => '[{"userID" : "Zach","userID" : "Trevor","userID" : "Phil","userID" : "Rob"}]')
        .then(res => res.json())
        .then((data) => {
          this.setState({ userID: data})
        })
        .catch(console.log)
  }

  onTextboxChangeFindOneValue(event) {
    this.setState({
      findOneValue: event.target.value,
    });
  }



  render() {
    const {
      isLoading,
      token,
      userID,
    } = this.state;

    if (isLoading) {
      return (<div><p>Loading...</p></div>);
    }

    if (!token) {
      return (
        <UserIDS userIDS={this.state.userID} />
        /*
        <div>
          <div>
            {this.map((this) => (
              <h5>{contact.userID}</h5>
            ))}
            <p>Load all users</p>
            <button onClick={() => alert(this.findOne)}>
              Load
            </button>
            <button onClick={this.findAll}>Load</button>
          </div>
          <br />
          <br />
          <div>
            <p>Find one user</p>
            <button onClick={this.findOne}>Where's Waldo</button>
          </div>
        </div>
        */
      );
    }
  }
}

export default Home;
