import React, { Component } from 'react';
import 'whatwg-fetch';

/*
import {
  //getFromStorage,
  getFromStor 1 ;
  setInStorage,
} from 'storage';
*/
class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
      token: '',
      userID: ''
    };

    this.onTextboxChangeFindOneValue = this.onTextboxChangeFindOneValue.bind(this);
  }

  componentDidMount() {
    //const obj = getFromStorage('the_main_app');
    const obj = 1 ;
    if (obj && obj.token) {
      const { token } = obj;
      // Verify token
      fetch('/api/account/verify?token=' + token)
        .then(res => res.json())
        .then(json => {
          if (json.success) {
            this.setState({
              token,
              isLoading: false
            });
          } else {
            this.setState({
              isLoading: false,
            });
          }
        });
    } else {
      this.setState({
        isLoading: false,
      });
    }
  }

  onTextboxChangeFindOneValue(event) {
    this.setState({
      findOneValue: event.target.value,
    });
  }

  render() {
    const {
      isLoading,
      token,
      userID,
    } = this.state;

    if (isLoading) {
      return (<div><p>Loading...</p></div>);
    }

    if (!token) {
      return (
        <div>
          <div>
            <p>Load all users</p>
            <button onClick={this.findAll}>Load</button>
          </div>
          <br />
          <br />
          <div>
            <p>Find one user</p>
            <button onClick={this.findOne}>Where's Waldo</button>
          </div>
        </div>
      );
    }
  }
}

export default Home;
